import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import withLayout from 'nextra-theme-docs'
import { withSSG } from 'nextra/ssg'
import layoutConfig from '/Users/penghaoen2/Documents/GitHub/blockchain-docs/theme.config.js'

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const MDXLayout = function NextraPage (props) {
    return withSSG(withLayout({
      filename: "index.md",
      route: "/",
      meta: {},
      pageMap: [{"name":"_app","route":"/_app"},{"name":"audits","children":[{"name":"certik","route":"/audits/certik"}],"route":"/audits"},{"name":"faqs","children":[{"name":"basics","route":"/faqs/basics"},{"name":"bonding","route":"/faqs/bonding"},{"name":"coupon-mechanics","route":"/faqs/coupon-mechanics"},{"name":"meta.json","meta":{"index":"Basics","bonding":"Bonding Tokens","coupon-mechanics":"Coupons","price-mechanics":"Price Mechanics"}},{"name":"price-mechanics","route":"/faqs/price-mechanics"}],"route":"/faqs"},{"name":"index","route":"/"},{"name":"meta.json","meta":{"index":"Home","faqs":"FAQs","audits":"Audits","resources":"Resources"}},{"name":"resources","route":"/resources"}]
    }, layoutConfig))(props)
}
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...props} components={components} mdxType="MDXLayout">
    <h1>{`Empty Set Dollar - ESD`}</h1>
    <p><img {...{
        "src": "/info.png",
        "parentName": "p"
      }} /></p>
    <h2>{`What is Empty Set Dollar?`}</h2>
    <p>{`Empty Set Dollar (ESD) is an algorithmic stablecoin built to be the reserve currency of Decentralized Finance. The protocol is centred around an `}
      <a {...{
        "href": "https://www.investopedia.com/news/what-erc20-and-what-does-it-mean-ethereum/",
        "parentName": "p"
      }}>{`ERC-20`}</a>
      {` token: `}
      <a {...{
        "href": "https://www.coingecko.com/en/coins/empty-set-dollar",
        "parentName": "p"
      }}>{`ESD`}</a>
      {`. This token acts both as the stabilized dollar and as the governance token for the protocol.`}</p>
    <p>{`In order to self-stabilize, the protocol uses voluntary supply expansions and contractions around a Time Weighted Average Price (TWAP) oracle from the incentivized trading pool on Uniswap.`}</p>
    <h2>{`Featuressssss`}</h2>
    <p>{`ESD has composed a number of different mechanics from various projects into a single protocol. This combination gives it unique features.`}</p>
    <ul>

      <li {...{
        "parentName": "ul"
      }}><strong {...{
          "parentName": "li"
        }}>{`Decentralised`}</strong>
        {` - Only upgradable via on-chain token governance`}</li>


      <li {...{
        "parentName": "ul"
      }}><strong {...{
          "parentName": "li"
        }}>{`Self-Stabilising`}</strong>
        {` - Uses an on-chain price oracle`}</li>


      <li {...{
        "parentName": "ul"
      }}><strong {...{
          "parentName": "li"
        }}>{`Composable`}</strong>
        {` - The token is a plain ERC-20 token which acts as a stablecoin & a governance token`}</li>


      <li {...{
        "parentName": "ul"
      }}><strong {...{
          "parentName": "li"
        }}>{`Opt-in Supply Adjustments`}</strong>
        {` - All supply expansions & contractions are incentivised & voluntary`}</li>

    </ul>
    <p><a {...{
        "href": "/resources",
        "parentName": "p"
      }}><strong {...{
          "parentName": "a"
        }}>{`See additional articles, guides and resources here`}</strong></a></p>

  </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
