import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import withLayout from 'nextra-theme-docs'
import { withSSG } from 'nextra/ssg'
import layoutConfig from '/Users/penghaoen2/Documents/GitHub/blockchain-docs/theme.config.js'

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const MDXLayout = function NextraPage (props) {
    return withSSG(withLayout({
      filename: "bonding.md",
      route: "/faqs/bonding",
      meta: {},
      pageMap: [{"name":"_app","route":"/_app"},{"name":"audits","children":[{"name":"certik","route":"/audits/certik"}],"route":"/audits"},{"name":"faqs","children":[{"name":"basics","route":"/faqs/basics"},{"name":"bonding","route":"/faqs/bonding"},{"name":"coupon-mechanics","route":"/faqs/coupon-mechanics"},{"name":"meta.json","meta":{"index":"Basics","bonding":"Bonding Tokens","coupon-mechanics":"Coupons","price-mechanics":"Price Mechanics"}},{"name":"price-mechanics","route":"/faqs/price-mechanics"}],"route":"/faqs"},{"name":"index","route":"/"},{"name":"meta.json","meta":{"index":"Home","faqs":"FAQs","audits":"Audits","resources":"Resources"}},{"name":"resources","route":"/resources"}]
    }, layoutConfig))(props)
}
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...props} components={components} mdxType="MDXLayout">
    <h1>{`Bonding ESD Tokens`}</h1>
    <h3>{`What is Bonding?`}</h3>
    <p>{`Bonding is the act of locking your token in the Empty Set Dollar DAO. You lock your tokens in the DAO in order to gain benefits such as voting or rewards.`}</p>
    <h3>{`What is Staging?`}</h3>
    <p>{`When entering the DAO, your tokens must pass through a staging phase. This state is used to control the deposit and withdrawal of tokens when your tokens are "fluid". See below to understand what "Frozen and Fluid" mean.`}</p>
    <h3>{`How do I bond ESD tokens?`}</h3>
    <h3>{`Why do some epochs grant rewards and some epochs do not?`}</h3>
    <p>{`If there is excess demand for ESD, then the price will trend above \\$1 on the Uniswap pool, and that signals the protocol to mint additional token supply. Conversely, if the demand shrinks, excess selling on Uniswap will push the price below $1, which triggers the protocol to generate debt and incentivise token holders to burn ESD to shrink the token supply.`}</p>
    <p>{`If the money supply needs to be shrunk, no supply expansion will be made that epoch. If the money supply was shrunk in the past, those debts will either remain on the protocol, or get bought by users in the form of coupons. Both coupons and debt will be paid off in the future ahead of new rewards, so it is possible to receive no new rewards in a money supply expansion epoch if there are historical debts to be repaid first.`}</p>
    <h3>{`What do frozen and fluid states mean?`}</h3>
    <p>{`The best way to visualize this is with the flow below:`}</p>
    <p><strong {...{
        "parentName": "p"
      }}>{`Deposit/Withdraw ←→ Staged ←→ Bond/Unbond.`}</strong></p>
    <ul>

      <li {...{
        "parentName": "ul"
      }}>{`Any time, you use Bond or Unbond once, you go into a fluid state in that epoch`}</li>


      <li {...{
        "parentName": "ul"
      }}>{`Upon the next epoch, you will go back into the frozen state`}</li>

    </ul>
    <p><strong {...{
        "parentName": "p"
      }}>{`In fluid state:`}</strong></p>
    <ul>

      <li {...{
        "parentName": "ul"
      }}>{`You will not be able to withdraw or deposit`}</li>


      <li {...{
        "parentName": "ul"
      }}>{`You will be able to bond/unbond any number of times.`}</li>

    </ul>
    <p><strong {...{
        "parentName": "p"
      }}>{`In Frozen state:`}</strong></p>
    <ul>

      <li {...{
        "parentName": "ul"
      }}>{`You will be able to deposit and withdraw.`}</li>

    </ul>
    <p>{`This means when you are bonded and want to withdraw, you will first unbond, spend the rest of the epoch in fluid state and upon next epoch when state changes to frozen, you will be able to withdraw.`}</p>
    <p>{`For Deposit, you will be able to deposit and bond in the same epoch as when you deposit and stage you will still be in a frozen state, but as soon as you bond, you will be in a fluid state, so you cannot deposit again in that epoch, or withdraw.`}</p>
    <p>{`Frozen and fluid states (also Locked) appear on the wallet page and the LP Reward Pool page of the DAO.  This terminology is a bit confusing, but you are basically allowed to interact with the DAO by bonding or unbonding ESD or LP just once per epoch.  Frozen and fluid tell you the state of your access to transacting with the DAO.  “Frozen” enables you to transact.  More state detail is discussed in the white paper.`}</p>

  </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
