import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import withLayout from 'nextra-theme-docs'
import { withSSG } from 'nextra/ssg'
import layoutConfig from '/Users/penghaoen2/Documents/GitHub/blockchain-docs/theme.config.js'

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const MDXLayout = function NextraPage (props) {
    return withSSG(withLayout({
      filename: "resources.md",
      route: "/resources",
      meta: {},
      pageMap: [{"name":"_app","route":"/_app"},{"name":"audits","children":[{"name":"certik","route":"/audits/certik"}],"route":"/audits"},{"name":"faqs","children":[{"name":"basics","route":"/faqs/basics"},{"name":"bonding","route":"/faqs/bonding"},{"name":"coupon-mechanics","route":"/faqs/coupon-mechanics"},{"name":"meta.json","meta":{"index":"Basics","bonding":"Bonding Tokens","coupon-mechanics":"Coupons","price-mechanics":"Price Mechanics"}},{"name":"price-mechanics","route":"/faqs/price-mechanics"}],"route":"/faqs"},{"name":"index","route":"/"},{"name":"meta.json","meta":{"index":"Home","faqs":"FAQs","audits":"Audits","resources":"Resources"}},{"name":"resources","route":"/resources"}]
    }, layoutConfig))(props)
}
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...props} components={components} mdxType="MDXLayout">
    <h1>{`Additional Resources`}</h1>
    <p>{`Below are a list of helpful resources about the Empty Set Dollar.`}</p>
    <h2>{`Offical Links`}</h2>
    <ul>

      <li {...{
        "parentName": "ul"
      }}><a {...{
          "href": "https://medium.com/d%C3%B8llar/introducing-d%C3%B8llar-and-the-ess-f48222b4e138",
          "parentName": "li"
        }}>{`Introducing d√∏llar and the ess`}</a></li>


      <li {...{
        "parentName": "ul"
      }}><a {...{
          "href": "https://github.com/emptysetsquad/dollar",
          "parentName": "li"
        }}>{`Empty Set Dollar's codebase`}</a></li>


      <li {...{
        "parentName": "ul"
      }}><a {...{
          "href": "https://github.com/emptysetsquad/dollar/raw/master/d%C3%B8llar.pdf",
          "parentName": "li"
        }}>{`Empty Set Dollar Whitepaper`}</a></li>

    </ul>
    <h2>{`Articles`}</h2>
    <ul>

      <li {...{
        "parentName": "ul"
      }}><a {...{
          "href": "https://medium.com/@lewisfreiberg/empty-set-dollar-esd-a0abbfc5ecdb",
          "parentName": "li"
        }}><em {...{
            "parentName": "a"
          }}>{`Empty Set Dollar - An experiment in decentralised, composable, oracle-driven stablecoins`}</em></a>
        {` by Lewis Freiberg`}</li>


      <li {...{
        "parentName": "ul"
      }}><a {...{
          "href": "https://complementcap.substack.com/p/the-empty-set-dollar-premium-superfluid",
          "parentName": "li"
        }}><em {...{
            "parentName": "a"
          }}>{`The Empty Set Dollar: Premium Superfluid Collateral`}</em></a>
        {` by Complement Capital`}</li>


      <li {...{
        "parentName": "ul"
      }}><a {...{
          "href": "https://www.delphidigital.io/reports/empty-set-dollar-a-game-theoretical-approach-to-elastic-stablecoins/",
          "parentName": "li"
        }}><em {...{
            "parentName": "a"
          }}>{`Empty Set Dollar: A Game Theoretical Approach to Elastic Stablecoins`}</em></a>
        {` by Delphi Digital (Paywalled)`}</li>

    </ul>
    <h2>{`Guides`}</h2>
    <ul>

      <li {...{
        "parentName": "ul"
      }}><a {...{
          "href": "https://medium.com/@0xans/ess-the-elastic-stablecoin-a-guide-to-bonding-to-earn-esds-b0eef9be7f9c",
          "parentName": "li"
        }}><em {...{
            "parentName": "a"
          }}>{`The elastic stablecoin. A guide to bonding to earn ESDs`}</em></a>
        {` by 0xAns`}</li>


      <li {...{
        "parentName": "ul"
      }}><a {...{
          "href": "https://www.youtube.com/watch?v=pANyq7mNoTg",
          "parentName": "li"
        }}><em {...{
            "parentName": "a"
          }}>{`Bonding and Unbonding Instructional Video`}</em></a>
        {` - The Bearded Prof`}</li>


      <li {...{
        "parentName": "ul"
      }}><a {...{
          "href": "https://www.youtube.com/watch?v=jCsRLC1rHT8&feature=youtu.be",
          "parentName": "li"
        }}><em {...{
            "parentName": "a"
          }}>{`Coupons Instructional Video`}</em></a>
        {` - The Bearded Prof`}</li>

    </ul>
    <h2>{`Tools`}</h2>
    <ul>

      <li {...{
        "parentName": "ul"
      }}><a {...{
          "href": "https://esd.tools",
          "parentName": "li"
        }}>{`ESD tools`}</a>
        {` - Simple statistics site for ESD`}</li>


      <li {...{
        "parentName": "ul"
      }}><a {...{
          "href": "https://esd.coupons",
          "parentName": "li"
        }}>{`ESD Coupons`}</a>
        {` - A website that enables you to authorise bots to redeem coupons on your behalf for a tip`}</li>


      <li {...{
        "parentName": "ul"
      }}><a {...{
          "href": "https://esd.money/",
          "parentName": "li"
        }}>{`ESD Money`}</a>
        {` - An application that lets you trade coupons without redeeming them`}</li>

    </ul>

  </MDXLayout>;
}

;
